import dslink
from twisted.internet import task


class PublishDSLink(dslink.DSLink):
    i=0
    def start(self):
        task.LoopingCall(self.publish).start(0.0001)

    def publish(self):
        #self.requester.invoke(path="/data/publish", params={"Path": "/data/test", "Value": "" + str(self.i), "CloseStream": True})
        self.requester.invoke(path="/data/publish", params={"Path": "/data/test", "Value": str(self.i), "CloseStream": True})
        self.i += 1

if __name__ == "__main__":
    PublishDSLink(dslink.Configuration("publish", requester=True, responder=True))

